import React from 'react'
import ReactDOM from 'react-dom'
import styled from 'styled-components'
import withRouter from 'react-router-dom/withRouter'
import {Link} from 'react-router-dom'
import {Wrapper, ClearBoth} from '../reusable-styles/index'
import logo from '../Header/AFRICABROWN.png'


const FixedHeaderWrap = styled.header`
    height: 62px;
`
const FixedHeader = styled.div`
    height: 62px;
    left: 0;
    top: 0;
    position: fixed;
    right: 0;
    z-index: 1000;
    @media screen and (min-width: 1600px) {
        margin: auto;
    }
`
const HeaderInner = styled.div`
    display: flex;
    align-items: center;
    margin: auto;
    width: 100%;
    box-sizing: border-box;
    height: 100%;
`

const HeaderLeft = styled.div`
    float: left;
    width: 60%;
    padding:20px;
`


const HeaderRight = styled.div`
    display:flex;
    float: right;
    width: 30%;
`

// const HeaderCenter = styled.form`
//     float: left;
//     width: 60%;
// `
const SearchInput = styled.input`
    height: 30px;
    width: 585px;
    border-radius: 15px;
    background-color: #F2F2F2;
    color: #949494;
    font-size: 13px;
    outline: none;
    border: none;
    box-sizing: border-box;
    padding: 0 16px;
    margin: auto;
    display: block;
    &::placeholder {
        color: #949494;
        font-size: 13px;
    }
`
const Text = styled.p`
    color: ${({theme}) => theme.colors.brown};
    padding-right: 40px;
    text-align: right;
    `

const Button = styled.a `
width
background: #402519;
background-image: -webkit-linear-gradient(top, #402519, #17191C);
background-image: -moz-linear-gradient(top, #402519, #17191C);
background-image: -ms-linear-gradient(top, #402519, #17191C);
background-image: -o-linear-gradient(top, #402519, #17191C);
background-image: -webkit-gradient(to bottom, #402519, #17191C);
-webkit-border-radius: 20px;
-moz-border-radius: 20px;
border-radius: 20px;
color: #FFFFFF;
font-family: Arial;
font-size: 15px;
font-weight: 100;
padding: 15px;
-webkit-box-shadow: 1px 1px 20px 0 #000000;
-moz-box-shadow: 1px 1px 20px 0 #000000;
box-shadow: 1px 1px 20px 0 #000000;
text-shadow: 1px 1px 20px #000000;
border: solid #000000 1px;
text-decoration: none;
display: inline-block;
cursor: pointer;
text-align: center;
`
class HeaderComponent extends React.Component {

  state = {
    profileEnable: false,
    searchEnable: false,
    notificationEnable: false
  }

  componentDidMount() {
  }
  componentWillUnmount() {
  }

  render() {
    return (
      <Wrapper>
        <FixedHeaderWrap>
          <FixedHeader>
            <HeaderInner>
              <HeaderLeft>
                <Link to="/home">
                  <img src={logo} alt="Logos" height="40px"  />
                </Link>
              </HeaderLeft>
              {/* <HeaderCenter>
                <SearchInput placeholder="Search university" type="text"/>
              </HeaderCenter> */}
              <HeaderRight>
                <Text>Explore</Text>
                <Text>Upload</Text>
                <Text>Country<span>NGN</span></Text>
              <Button>SignUp</Button>
              </HeaderRight>
              <ClearBoth/>
            </HeaderInner>
          </FixedHeader>
        </FixedHeaderWrap>
      </Wrapper>
    )
  }
}

export default withRouter(HeaderComponent);
