import React from 'react'
import ReactDOM from 'react-dom'
import styled from 'styled-components'
import withRouter from 'react-router-dom/withRouter'
import {Link} from 'react-router-dom'
import {Wrapper, ClearBoth} from '../reusable-styles/index'
// import logo1 from '../../../assets/black-and.jpg'

const FixedHeaderWrap = styled.header`
    height: 62px;
`
const FixedHeader = styled.div`
    background-color: ${({theme}) => theme.colors.white};
    height: 62px;
    width: 100%;
    left: 0;
    top: 0;
    position: fixed;
    right: 0;
    z-index: 1000;
    @media screen and (min-width: 1600px) {
        width: 1370px; 
        margin: auto;
    }
`
// const BorderBottom = styled.div`
//     height: 3%;
// background: linear-gradient(135.82deg, #FEAC5E 0%, #C779D0 52.28%, #4BC0C8 100%);
//     width: 100%;
// `

const HeaderInner = styled.div`
    display: flex;
    align-items: center;
    margin: auto;
    width: 1141px;
    box-sizing: border-box;
    height: 100%;
`

const HeaderLeft = styled.div`
    float: left;
    width: 20%
`

const HeaderCenter = styled.form`
    float: left;
    width: 60%;
`
const SearchInput = styled.input`
    height: 30px;
    width: 585px;
    border-radius: 15px;
    background-color: #F2F2F2;
    color: #949494;
    font-size: 13px;
    outline: none;
    border: none;
    box-sizing: border-box;
    padding: 0 16px;
    margin: auto;
    display: block;
    &::placeholder {
        color: #949494;
        font-size: 13px;
    }
`
const HeaderRight = styled.div`
    float: right;
    width: 20%;
`

const AccountDiv = styled.div`
    width: 35px;
    height: 35px;
    border-radius: 100%;
    background-position: center;
    background-size: contain;
    background-repeat: no-repeat;
    float: right;
    cursor: pointer;
    position: relative;
`
const AccountDivInner = styled.div`
    width: 100%;
    height: 100%;
`
const NotificationBtn = styled.div`
    height: 35px;
    width: 35px;
    border-radius: 100%;
    color: rgb(169, 172, 180);
    text-align: center;
    float: right;
    display: table;
    margin-right: 20px;
    position: relative;
    line-height: 33px;
`




class HeaderComponent extends React.Component {

  state = {
    profileEnable: false,
    searchEnable: false,
    notificationEnable: false
  }

  componentDidMount() {

  }

  componentWillUnmount() {

  }




  render() {
    return (
      <Wrapper>
        <FixedHeaderWrap>
          <FixedHeader>
            <HeaderInner>
              <HeaderLeft>
                <Link to="/home">
                  <img src={require('../../../assets/images/AFRICABROWN.png')} alt="Logos"/>
                </Link>
              </HeaderLeft>
              <HeaderCenter>
                <SearchInput placeholder="Search university" type="text"/>
              </HeaderCenter>
              <HeaderRight>
                <ClearBoth/>
              </HeaderRight>
              <ClearBoth/>
            </HeaderInner>
          </FixedHeader>
        </FixedHeaderWrap>
      </Wrapper>
    )
  }
}

export default withRouter(HeaderComponent);
