import React from 'react'
import ReactDOM from 'react-dom'
import styled from 'styled-components'
import withRouter from 'react-router-dom/withRouter'
import {Link} from 'react-router-dom'
import {Wrapper, ClearBoth} from '../reusable-styles/index'
import logo from '../Header/AFRICABROWN.png'


const FixedHeaderWrap = styled.header`
    height: 62px;
`
const FixedHeader = styled.div`
    background-color: ${({theme}) => theme.colors.white};
    height: 62px;
    width: 100%;
    left: 0;
    top: 0;
    position: fixed;
    right: 0;
    z-index: 1000;
    @media screen and (min-width: 1600px) {
        width: 100%; 
        margin: auto;
    }
`
const HeaderInner = styled.div`
    display: flex;
    align-items: center;
    margin: auto;
    width: 1141px;
    box-sizing: border-box;
    height: 100%;
`

const HeaderLeft = styled.div`
    float: left;
    width: 20%;
    padding:20px
`

const HeaderCenter = styled.form`
    float: left;
    width: 60%;
`
const SearchInput = styled.input`
    height: 30px;
    width: 585px;
    border-radius: 15px;
    background-color: #F2F2F2;
    color: #949494;
    font-size: 13px;
    outline: none;
    border: none;
    box-sizing: border-box;
    padding: 0 16px;
    margin: auto;
    display: block;
    &::placeholder {
        color: #949494;
        font-size: 13px;
    }
`
const HeaderRight = styled.div`
    float: right;
    width: 20%;
`

class HeaderComponent extends React.Component {

  state = {
    profileEnable: false,
    searchEnable: false,
    notificationEnable: false
  }

  componentDidMount() {
  }
  componentWillUnmount() {
  }




  render() {
    return (
      <Wrapper>
        <FixedHeaderWrap>
          <FixedHeader>
            <HeaderInner>
              <HeaderLeft>
                <Link to="/home">
                  <img src={logo} alt="Logos" height="40px"  />
                </Link>
              </HeaderLeft>
              <HeaderCenter>
                <SearchInput placeholder="Search university" type="text"/>
              </HeaderCenter>
              <HeaderRight>
                <ClearBoth/>
              </HeaderRight>
              <ClearBoth/>
            </HeaderInner>
          </FixedHeader>
        </FixedHeaderWrap>
      </Wrapper>
    )
  }
}

export default withRouter(HeaderComponent);
