import React from 'react'
import styled from 'styled-components'
import withRouter from 'react-router-dom/withRouter'
import {Link} from 'react-router-dom'
import {Wrapper, ClearBoth} from '../reusable-styles/index'

const FixedFooterWrap = styled.footer`
    height: 62px;
`
const FixedFooter = styled.div`
    background-color: ${({theme}) => theme.colors.black};
    height: 62px;
    width: 100%;
    left: 0;
    bottom: 0;
    position: fixed;
    right: 0;
    z-index: 1000;
    @media screen and (min-width: 1600px) {
        width: 100%; 
        margin: auto;
    }
`
const FooterInner = styled.div`
    display: flex;
    align-items: center;
    margin: auto;
    width: 1141px;
    box-sizing: border-box;
    height: 100%;
`

const FooterLeft = styled.div`
    float: left;
    width: 20%
`

const FooterCenter = styled.form`
    float: left;
    width: 60%;
`
const SearchInput = styled.input`
    height: 30px;
    width: 585px;
    border-radius: 15px;
    background-color: #F2F2F2;
    color: #949494;
    font-size: 13px;
    outline: none;
    border: none;
    box-sizing: border-box;
    padding: 0 16px;
    margin: auto;
    display: block;
    &::placeholder {
        color: #949494;
        font-size: 13px;
    }
`
const FooterRight = styled.div`
    float: right;
    width: 20%;
`

class FooterComponent extends React.Component {

  state = {
    profileEnable: false,
    searchEnable: false,
    notificationEnable: false
  }

  componentDidMount() {
  }
  componentWillUnmount() {
  }




  render() {
    return (
      <Wrapper>
        <FixedFooterWrap>
          <FixedFooter>
            <FooterInner>
              <FooterLeft>
                <Link to="/home">
                  <img src={require('../../../assets/images/AFRICABROWN.png')} alt="Logos"/>
                </Link>
              </FooterLeft>
              <FooterCenter>
                <SearchInput placeholder="Search university" type="text"/>
              </FooterCenter>

              <ClearBoth/>
            </FooterInner>
          </FixedFooter>
        </FixedFooterWrap>
      </Wrapper>
    )
  }
}

export default withRouter(FooterComponent);
